import logo from './logo.svg';
import './App.css';
import ChatBox from "./components/ChatBox";
import React, { useState } from "react";

function App() {



  const [sessionId, setSessionId] = useState(null);
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);

  const handleUpload = async (e) => {
    const formData = new FormData();
    formData.append("file", e.target.files[0]);

    const res = await fetch("http://127.0.0.1:8000/upload_pdf/", {
      method: "POST",
      body: formData,
    });

    const data = await res.json();
    setSessionId(data.session_id);
  };

  const handleAsk = async () => {
    if (!sessionId || !question.trim()) return;

    const formData = new FormData();
    formData.append("session_id", sessionId);
    formData.append("question", question);

    const res = await fetch("http://127.0.0.1:8000/chat/", {
      method: "POST",
      body: formData,
    });

    const data = await res.json();

    setMessages((prev) => [...prev, { q: question, a: data.answer }]);
    setQuestion("");
  };
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
      <div style={{ maxWidth: 600, margin: "50px auto", fontFamily: "Arial, sans-serif" }}>
      <h2>Chat PDF con Gemini</h2>
      <ChatBox />



    </div>

    <div className="p-6 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Chat con PDF</h1>

      {!sessionId && (
        <div>
          <p className="mb-2">Subí un PDF para empezar:</p>
          <input type="file" accept="application/pdf" onChange={handleUpload} />
        </div>
      )}

      {sessionId && (
        <div className="mt-6">
          <p className="mb-2">Sesión activa: {sessionId}</p>
          <div className="flex gap-2">
            <input
              className="flex-1 border p-2 rounded"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              placeholder="Escribí tu pregunta..."
            />
            <button
              onClick={handleAsk}
              className="bg-blue-500 text-white px-4 py-2 rounded"
            >
              Preguntar
            </button>
          </div>

          <div className="mt-4 space-y-3">
            {messages.map((m, i) => (
              <div key={i} className="border p-2 rounded">
                <p className="font-semibold">Tú: {m.q}</p>
                <p className="text-gray-700">Bot: {m.a}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>


    </div>
  );
}

export default App;
